defmodule AdventOfCode.Day1Test do
  use ExUnit.Case
  alias AdventOfCode.Day1

  test "easy" do
    assert Day1.handle_input("") == 0
    assert Day1.handle_input("()") == 0
    assert Day1.handle_input("(())") == 0
    assert Day1.handle_input(")(") == 0
    assert Day1.handle_input("))((") == 0

    assert Day1.handle_input(")") == -1
    assert Day1.handle_input("(") == 1
    assert Day1.handle_input("))") == -2
    assert Day1.handle_input("((") == 2

    assert Day1.handle_input("(()") == 1
    assert Day1.handle_input("())") == -1
    assert Day1.handle_input(")((") == 1
    assert Day1.handle_input("))(") == -1
  end

